INSERT INTO `himall_gifts` VALUES ('68', '巧克力', '520', '1', '520', '2017-03-14 10:39:44', '0', '0', '0', '1', '/Storage/Gift/68', '100', '52.00', '<p><span style=\"color: rgb(51, 51, 51); font-family: arial, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px; background-color: rgb(255, 255, 255);\">巧克力是一个外来词Chocolate的译音(粤港澳粤语地区译为“朱古力”）。主原料是可可豆（像椰子般的果实，在树干上会开花结果），它的起源甚早，始于墨西哥极盛一时的阿斯帝卡王朝最后一任皇帝孟特儒 ，当时是崇拜巧克力的社会，喜欢以辣椒、番椒、香草豆和香料添加在饮料中，打起泡沫，并以黄金杯子每天喝50CC，是属于宫廷成员的饮料，它的学名Theobroma有“众神的饮料”之意，被视为贵重的强心、利尿的药剂，它对胃液中的蛋白质分解酵素具有活化性的作用，可帮助消化。</span></p>', '2017-02-14 10:44:15');
